mapper.select:
SELECT
shop_name,shop_img,business_license_img,business_license_code,phone,province,city,district,
full_address,shop_more_info,is_mobile,open_time,close_time,profile_img,coordinate_x,coordinate_y,
enable_status,business_scope,create_time,last_edit_time
FROM
tb_shop


		and shop_name like '%${shopCondition.shopName}%'


		and enable_status = #{shopCondition.enableStatus}


		and province = #{shopCondition.province}


		and city = #{shopCondition.city}


		and district = #{shopCondition.district}


		and is_mobile = #{shopCondition.isMobile}


		and user_id = #{shopCondition.userId}


LIMIT #{rowIndex},#{pageSize};

SELECT
count(1)
FROM
tb_shop


		and shop_name like '%${shopCondition.shopName}%'


		and enable_status = #{shopCondition.enableStatus}


		and province = #{shopCondition.province}


		and city = #{shopCondition.city}


		and district = #{shopCondition.district}


		and is_mobile = #{shopCondition.isMobile}


		and user_id = #{shopCondition.userId}

mapper.update:
update tb_shop
shop_name=#{shopName}, shop_img=#{shopImg}, business_license_img=#{businessLicenseImg}, business_license_code=#{businessLicenseCode}, phone=#{phone}, province=#{province}, city=#{city}, district=#{district}, full_address=#{fullAddress}, shop_more_info=#{shopMoreInfo}, is_mobile=#{isMobile}, open_time=#{openTime}, close_time=#{closeTime}, profile_img=#{profileImg}, coordinate_x=#{coordinateX}, coordinate_y=#{coordinateY}, business_scope=#{businessScope}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}
where shop_id=#{shopId}
